//login button
    private void login_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_login_buttonActionPerformed
        
        
        
        if(user_login.getText().trim().isEmpty() && pass_login.getText().trim().isEmpty() ){
            username_error.setText("*enter username");
            password_error.setText("*enter password");
        }
        else if(user_login.getText().trim().isEmpty())
            username_error.setText("*enter username");
        else if(pass_login.getText().trim().isEmpty())
            password_error.setText("*enter password");
        else{
            String log_user=user_login.getText();
            String log_pass=pass_login.getText();
            try {
                Class.forName("oracle.jdbc.OracleDriver");
                Connection con=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:XE", "Project", "shane95");
                String query="select * from REGISTRATION where USERNAME=? and PASSWORD=?";
                PreparedStatement pre=con.prepareStatement(query);
                pre.setString(1,log_user);
                pre.setString(2,log_pass);
                ResultSet re=pre.executeQuery();
                if(re.next()){
                    MainPage r =new MainPage(log_user);
                    new BeerPage(log_user);
                    r.setVisible(true);
                    this.dispose();
                }
                else{
                    JOptionPane.showMessageDialog(null,"Incorrect username or password");
                }
                }
            catch (ClassNotFoundException | SQLException ex) {
                Logger.getLogger(LoginPage.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
 
    }//GEN-LAST:event_login_buttonActionPerformed

