    private void date_of_birthFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_date_of_birthFocusLost
        if(date_of_birth.getText().trim().equals("") || date_of_birth.getText().trim().toLowerCase().equals("dd/mm/yyyy")){
            date_of_birth.setText("dd/mm/yyyy");
            date_of_birth.setForeground(new Color(153,153,153));
        }
    }//GEN-LAST:event_date_of_birthFocusLost

    private void date_of_birthFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_date_of_birthFocusGained
        if(date_of_birth.getText().trim().toLowerCase().equals("dd/mm/yyyy")){
            date_of_birth.setText("");
            date_of_birth.setForeground(Color.black);
        }
    }//GEN-LAST:event_date_of_birthFocusGained

