//create button       
    private void create_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_create_buttonActionPerformed

        
        String user=username.getText();
        String name=namebox.getText();
        String pass=password.getText();
        String email=e_mail.getText();
        String cpass=cpassword.getText();
        String addr=address.getText();
        String phn=phone.getText();
        String dob=date_of_birth.getText();
        String secques = security_box.getSelectedItem().toString();
        String answer = answer_sec.getText();
        
        String pattern = "(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=])(?=\\S+$).{8,}";

        if(user.trim().isEmpty() || name.trim().isEmpty() || email.trim().isEmpty()
        || pass.trim().isEmpty() || cpass.trim().isEmpty() || addr.trim().isEmpty()
        || phn.trim().isEmpty()  || answer.trim().isEmpty() ){
            if(user.trim().isEmpty())
                user_error.setText("*enter username");
            if(name.trim().isEmpty())
                name_error.setText("*enter name");
            if(email.trim().isEmpty())
                email_error.setText("*enter email");
            if(pass.trim().isEmpty())
                pass_error.setText("*enter password");
            if(cpass.trim().isEmpty())
                cpass_error.setText("*confirm password");
            if(phn.trim().isEmpty())
                phone_error.setText("*enter phone number");
            if(addr.trim().isEmpty())
                address_error.setText("*enter address");
            if(!(dob.trim().isEmpty()))
                dob_error.setText("*enter date of birth");
            if(answer.trim().isEmpty())
                ans_error.setText("*enter answer");
        }
        else if(!cpass.equals(pass)){
            cpass_error.setText("*passwords don't match");
            pass_error.setText("*passwords don't match");
        }
        else if(!pass.matches(pattern))
        JOptionPane.showMessageDialog(null,"password does not meet the requirements");
        else{
            String min_year= "1997";
            String dob_ar[] = dob.split("/");
            int year = min_year.compareTo(dob_ar[2]);
            if(year < 0)
            JOptionPane.showMessageDialog(null,"You have to be above 21 years to create an account ");
            else{
                try {
                    Class.forName("oracle.jdbc.OracleDriver");
                    Connection con=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:XE", "Project", "shane95");
                    Statement stat=con.createStatement();
                    
                    stat.executeUpdate("insert into REGISTRATION(USERNAME,NAME,EMAIL,PASSWORD,ADDRESS,PHONE,DOB,SECURITY_QUESTION,SECURITY_ANSWER) values"
                        + "('"+user+"','"+name+"','"+email+"','"+pass+"','"+addr+"','"+phn+"','"+dob+"','"+secques+"','"+answer+"')     ");
                    LoginPage r=new LoginPage();
                    r.setVisible(true);
                    this.dispose();
                }catch(ClassNotFoundException | SQLException ex){
                    Logger.getLogger(Registration.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }

    }//GEN-LAST:event_create_buttonActionPerformed

